import React from "react";
import { NavLink } from "react-router-dom";
import FontSizeChanger from "react-font-size-changer";

export const Navbar: React.FunctionComponent = () => (
  <nav>
    <div className="nav-wrapper deep-orange darken-4 px1">
      <a href="/" className="brand-logo">
        Grandpa test
      </a>

      <ul id="nav-mobile" className="right hide-on-med-and-down">
        <li>
            <i className="material-icons left">font_download</i>  <i className="tiny material-icons left">font_download</i> Increase/ Decrease font
            size{" "}
        </li>

        <li>
          <NavLink to="/">
            <i className="material-icons left">volume_up</i>Read the page to me
          </NavLink>
        </li>

        <li>
          <NavLink to="/contact">
            <i className="material-icons left">email</i>Contact
          </NavLink>
        </li>
      </ul>


      <div className="app">
        <FontSizeChanger
          targets={['#target .content']}
          onChange={() => {
            console.log();
          }}
          options={{
            stepSize: 2,
            range: 3
          }}
          customButtons={{
            up: <span style={{'fontSize': '36px'}}>A</span>,
            down: <span style={{'fontSize': '20px'}}>A</span>,
            style: {
              backgroundColor: 'red',
              color: 'white',
              WebkitBoxSizing: 'border-box',
              WebkitBorderRadius: '5px',
              width: '60px'
            },
            buttonsMargin: 10
          }}          
        />
        <div id="target">
          <p className="title">This is the title of my target text</p>
          <p className="content">This is the content of my target text</p>
        </div>
      </div>


    </div>
  </nav>
);
